import React from "react";
import styled from "styled-components";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
// import { useForm } from "react-hook-form";


const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  width: 100%;
  height: 100%;
`;

const Label = styled.label`
  font-family: "Exo 2", sans-serif;
  font-size: 1rem;
  font-weight: 200;
  color: #fff;
`;

const Input = styled.input`
  font-family: "Exo 2", sans-serif;
  font-size: 1rem;
  font-weight: 700;
  color: #fff;
  border-radius: 20px;
  background-color:#ffffffad;
  text-align: center;
`;

const Button = styled.button`
  font-family: "Exo 2", sans-serif;
  font-size: 1rem;
  font-weight: 200;
  color: #fff;
  height: 45px;
  width: 85px;
  border: 0px solid;
  background-image: linear-gradient(271deg, blue, #00f0a0, blue,#00f0a0);
  background-size: 500% 400%;
  border-radius: 50px;
  transition: 0.6s all;

  &:hover {
    background-position: 75% 50%;
    transform: perspective(100px);
  }

  &:active {
    transform: scale(0.95);
    transition: 0.1s;
  }
`;

const H1 = styled.h1`
  font-family: "Eczar", serif;
  font-size: 2rem;
  font-weight: 500;
  color: #fff;
`;

const initialState = {
  email: "",
  password: "",
  confirmPassword: ""
};

export default function SignUpForm() {
  const [formData, setFormData] = useState(initialState);
  const [isPasswordCorrect, setIsPasswordCorrect] = useState(true);
  const navigate = useNavigate();

  const handleChange = (event:any) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const validatePassword = (password:string, confirmPassword:string) => {
    if (password !== confirmPassword) {
      alert("Passwords do not match");
      setIsPasswordCorrect(false);
    } else {
      setIsPasswordCorrect(true);
    }}


  const handleSubmit = (e:any) => {
    e.preventDefault();
    validatePassword(formData.password, formData.confirmPassword);
    console.log(formData);
    navigate("/user");  //redirect to user page
  };

  return (
    <>
      <H1>Register</H1>
      <Form onSubmit={handleSubmit}>
        <Label>Email/ Username</Label>
        <Input type="text" placeholder="Email" onChange={handleChange} />
        <Label>Password</Label>
        <Input type="password" placeholder="Password" id="password" onChange={handleChange} />
        <Label>{isPasswordCorrect? "Confirm Password" : "Passwords do not match"}</Label>
        <Input type="password" placeholder="Password" id="confirmPassword" onChange={handleChange} />
        <Button type="submit">SignUp</Button>
      </Form>
    </>
  );
}
